import express from 'express';
import loginRoute from './routes/LoginRoute';
import 'express-async-errors';
import cors from 'cors';
import HttpErrorMiddleware from './middlewares/HttpErrorMiddleware';
// import swaggerUi from 'swagger-ui-express';/
// import swaggerDocument from '../swagger-output.json';

class App {
  public app: express.Express;

  constructor() {
    this.app = express();
    this.config();

    this.app.use(cors());

    this.app.use('/login', loginRoute);
    this.app.get('/', (_req, res) => res.json({ ok: true }));

    this.app.use(HttpErrorMiddleware);
  }

  private config(): void {
    const accessControl: express.RequestHandler = (_req, res, next) => {
      res.header('Access-Control-Allow-Origin', '*');
      res.header('Access-Control-Allow-Methods', 'GET,POST,DELETE,OPTIONS,PUT,PATCH');
      res.header('Access-Control-Allow-Headers', '*');
      next();
    };

    this.app.use(express.json());
    this.app.use(accessControl);
  }

  public start(PORT: string | number): void {
    this.app.listen(PORT, () => console.log(`Running on port ${PORT}`));
  }
}

export default App;

import { Router } from 'express';
import LoginController from '../controllers/LoginController';
import loginMiddleware from '../middlewares/loginMiddleware';

const router = Router();

router.post('/', loginMiddleware, LoginController.login);

export default router;

import { Request, Response, NextFunction,} from 'express';
import LoginService from '../services/LoginService';

export default class LoginController {
  public static async login(req: Request, res: Response, next: NextFunction) {
    try {
      const token = await LoginService.login(req.body);
      res.status(200).json({ message: 'Login realizado com sucesso', token });
    } catch (error) {
      next(error);
    }
  }
}

import HttpException from "../utils/HttpException";
import UserModel from "../database/models/UserModel";
import Ilogin, { Itoken } from '../interfaces/Ilogin';
import signToken from "../utils/SignToken";
import md5 from 'md5';

export default class LoginServices {
    public static async login(login: Ilogin): Promise<string> {
        const user = await UserModel.findOne({ where: { email: login.email } });
        const md5DescLogin = md5(login.password);
        if (!user || md5DescLogin !== user.password) {
            throw new HttpException(401, 'Incorrect email or password');
        }
        return signToken(login);
    }
}

class HttpException extends Error {
  status: number;
  message: string;

  constructor(status: number, message: string) {
    super(message);
    this.status = status;
    this.message = message;
  }
}

export default HttpException;

