class User extends Model {
  declare public id: number;
  declare public name: string;
  declare public email: string;
  declare public password: string;
  declare public role: string;
}

  User.init({
    id: {
      type: INTEGER,
      autoIncrement: true,
      primaryKey: true,
    },
    name: {
      type: STRING,
      allowNull: false,
    },
    email: {
      type: STRING,
      allowNull: false,
    },
    password: {
      type: STRING,
      allowNull: false,
    },
    role: {
      type: STRING,
      allowNull: false,
    },
  }, {
    sequelize: db,
    modelName: 'User',
    tableName: 'users',
    underscored: true,
    timestamps: false,
  });

export default User;

import { Sequelize } from 'sequelize';
import * as config from '../config/database';

const sequelize = new Sequelize(config)

export default sequelize;

import * as jsonwebtoken from 'jsonwebtoken';
import Ilogin from '../interfaces/Ilogin';

export default function signToken(ilogin: Ilogin) {
  const jwt = jsonwebtoken;
  const passSecret = process.env.JWT_SECRET as string;
  const payload = {
    name: ilogin.name,
    email: ilogin.email,
  };
  return jwt.sign(payload, passSecret);
}

import { Request, Response, NextFunction } from 'express';
import HttpException from '../utils/HttpException';
import Joi from 'joi';

const joi = Joi.object({
  email: Joi.string().email().required(),
  password: Joi.string().min(6).required(),
});

const loginValidation = (
  req: Request,
  res: Response,
  next: NextFunction,
) => {
  const { email, password } = req.body;
  try {
    joi.validate({ email, password });
  } catch (error) {
    throw new HttpException(400, 'E-mail and password error');;
  }
  next();
};

export default loginValidation;

import { Request, Response, NextFunction } from 'express';
import HttpException from '../utils/HttpException';

const httpErrorMiddleware = (
  erro: Error,
  _req: Request,
  res: Response,
  _next: NextFunction,
) => {
  const { status, message } = erro as HttpException;
  res.status(status || 500).json({ message });
};

export default httpErrorMiddleware;

import * as jwt from 'jsonwebtoken';
import HttpException from './HttpException';
import 'dotenv/config';

export default function verifyToken(token: string) {
  const secret = process.env.JWT_SECRET as unknown as string;
  try {
    const verifyTokens = jwt.verify(token, secret);
    return verifyTokens;
  } catch (error) {
    throw new HttpException(401, 'Token must be a valid token');
  }
}
