import  * as jsonwebtoken from 'jsonwebtoken';
import Ilogin from '../interfaces/Ilogin';
import 'dotenv/config';

export default function signToken(ilogin: Ilogin) {
  const { name, email, role } = ilogin;
  const payload = { name, email, role };
  const jwt = jsonwebtoken;
  const JWT_SECRET = process.env.JWT_SECRET as jsonwebtoken.Secret;
  return jwt.sign(payload, JWT_SECRET,
    {
      expiresIn: '30d',
      algorithm: 'HS256'
    });
}

import { Request, Response, NextFunction } from 'express';
import HttpException from '../utils/HttpException';
import Joi from 'joi';

const joi = Joi.object({
  email: Joi.string().email().required(),
  password: Joi.string().min(6).required(),
});

const loginValidation = (
  req: Request,
  res: Response,
  next: NextFunction,
) => {
  const { email, password } = req.body;
  try {
    joi.validate({ email, password });
  } catch (error) {
    throw new HttpException(400, 'E-mail and password error');;
  }
  next();
};

export default loginValidation;

import { Router } from 'express';
import { UserController } from '../controllers/UserController';

const router = Router();

router.post('/', UserController.userName);

export default router;

import { Request, Response, NextFunction,} from 'express';
import { userServices as UserService } from '../services/UserService';

export class UserController {
  public static async userName(req: Request, res: Response, next: NextFunction) {
    try {
      const userName = await UserService.userName(req.body);
      res.status(200).json(userName);
    } catch (error) {
      next(error);
    }
  }
}

import HttpException from "../utils/HttpException";
import UserModel from "../database/models/UserModel";
import { verifyToken } from "../utils/VerifyToken";

export class userServices {
    public static async userName(token: string): Promise<UserModel> {
    const retornVerify = verifyToken(token);
    const userName = await UserModel.findOne({
        attributes: ['name'],
        where: { email: retornVerify.email },
    });
    if (!userName) {
        throw new HttpException(401, 'Incorrect email or token');
    }
    return userName;
    }
}

import jwt from 'jsonwebtoken';
import HttpException from './HttpException';
import 'dotenv/config';

export function verifyToken({ token }: any): any {
  const JWT_SECRET = process.env.JWT_SECRET as jwt.Secret;
  try {
    return jwt.verify(token, JWT_SECRET);
  } catch (error) {
    throw new HttpException(401, 'Token must be a valid token');
  }
}

import { Request, Response, NextFunction } from 'express';
import HttpException from '../utils/HttpException';

const httpErrorMiddleware = (
  erro: Error,
  _req: Request,
  res: Response,
  _next: NextFunction,
) => {
  const { status, message } = erro as HttpException;
  res.status(status || 500).json({ message });
};

export default httpErrorMiddleware;
